.solid-simple-table.linter {
  color: #787d9b;
  font-size: 11px;
}
.solid-simple-table.linter thead {
  background-color: #21222c;
  user-select: none;
}
.solid-simple-table.linter tbody {
  background-color: #272935;
}
.solid-simple-table.linter td,
.solid-simple-table.linter th {
  padding: 3px 10px;
}
.solid-simple-table.linter td {
  min-width: 100px;
  border: 1.57142857px solid #21222c;
}
.solid-simple-table.linter td:nth-child(4),
.solid-simple-table.linter td:nth-child(5) {
  cursor: pointer;
}
.solid-simple-table.linter.error {
  border-left: 4px solid #ff5757;
  padding-left: 8px;
}
.solid-simple-table.linter.warning {
  border-left: 4px solid #bf95f9;
  padding-left: 8px;
}
.solid-simple-table.linter.info {
  border-left: 4px solid #8be8fd;
  padding-left: 8px;
}
